<?phpif ( ! defined( 'ABSPATH' ) ) {	exit; // Exit if accessed directly}	abstract class POD_Twilio_Admin {			/**		*	@var integer	*	@protected	* 	total twilio users	**/	protected $_total_users;		/**		*	@var integer	*	@protected	* 	total supported contries	**/	protected $_total_countries;		/**	*	@var string	*	@protected	*	displayed message	**/	protected $_message;		/**	*	@var string	*	@protected	*	message type	**/	protected $_message_type;		/**	*	@var string	*	@protected	*	current active tab slug	**/	protected $active_tab;		/**	* 	@var integer	*	@protected	* 	limit variable for pagination	*/	protected $_limit;		/*	*	@protected	*	instance of global $wpdb for accessing database	*/	protected $_db;		/**	* constructor of this class	**/	public function __construct() {		global $wpdb;		$this->_db = $wpdb;		$this->_total_users = pod_twilio_count_total_table_rows( POD_TWILIO_USERS );		$this->_total_countries = pod_twilio_count_total_table_rows( POD_TWILIO_SUPPORTED_COUNTRIES );	}		/**	*	display message and unset the message variables	**/	public function pod_twilio_show_message(){		if( $this->_message ){			if( $this->_message_type == "error" ){				$type = "error";			}			else{				$type = "updated";			}?>			<div id="message" class="<?php echo $type; ?> notice is-dismissible">				<p><?php echo $this->_message; ?></p>				<button type="button" class="notice-dismiss"><span class="screen-reader-text"><?php esc_html_e( "Dismiss this notice.", POD_TWILIO_TEXT_DOMAIN ); ?></span></button>			</div><?php		}		unset($this->message);		unset($this->message_type);	}} ?>