<?phpif ( ! defined( 'ABSPATH' ) ) {	exit; // Exit if accessed directly}use Twilio\Rest\Client;class POD_Twilio_User_Logs {		/*	*	@var integer	*	@private	*	user id	*/	private $_id;		/*		*	@array	*	@private	*	user details of of provided user id	*/	private $_user;			public function __construct( $user_id ){		$this->_id = $user_id;		$this->_user = new POD_Twilio_User( $this->_id );		$this->_client = new Client( $this->_user->account_sid, $this->_user->account_auth_token );	}		public function get_user_call_logs(){		$call_logs = $this->_client->calls->stream();		return $call_logs;	}		public function total_call_logs(){		$usage_records = $this->_client->usage->records->read(														array(															"category" => "calls"														)													);		$count = 0;		foreach( $usage_records as $record ){			if( $record->category == "calls" ){				$count = $record->count;				break;			}				}				return $count;	}}